import React, { Component } from 'react'
import axios from 'axios'
import Input from './Addtodo'
export class Todo extends Component {
    state={
        todos:[]
    }
    componentDidMount(){
        this.getTodo()
    }
 getTodo=()=>{
     axios.get('/todos').then(response=>{
        this.setState({
            todos:response.data
        })
     }).catch(error=>{
         console.log(error)
     })
 }
    render() {
        return (
            <div>
        <h1>My Todo(s)</h1>
        <Input getTodo={this.getTodo}/>
            </div>
        )
    }
}

export default Todo
