import React, { Component } from 'react'

export class List extends Component {
    render() {
        const TodoList=this.props.todos?(
            this.props.todos.map(todo=>{
                return (
                    <div className='card-panel'>
                       <span class="blue-text text-darken-2"> {todo.title}</span> 
                    </div>
                )
            })
       
        ):
        (
        <p> No todos left</p>
        )
        return(
            <div> {TodoList}</div>
        )
    }
}

export default List
