    }
    componentDidMount(){
        axios.get('/todos').then(response=>{
            this.setState({
                todos:response.data
            })
         }).catch(error=>{
             console.log(error)
         })
    }
 
    render() {
        const {todos}=this.state
        return (
            <div>
        <h1>My Todo(s)</h1>
        <Addtodo getTodo={this.getTodo}/>
        <List todos={todos}/>
            </div>
        )
    }
}

export default Todo
